import React, { useState } from 'react';
import axios from 'axios';
import './styles.css';

const App = () => {
    const [name, setName] = useState('');
    const [rating, setRating] = useState(0);
    const [loveMessage, setLoveMessage] = useState('');
    const [thankYouMessage, setThankYouMessage] = useState('');

    // Handle form submission
    const handleSubmit = (event) => {
        event.preventDefault();

        const formData = {
            name: name,
            rating: rating,
            loveMessage: loveMessage
        };

        // Send the form data to the backend using axios
        axios.post('http://localhost:5000/submit-form', formData)
            .then((response) => {
                console.log(response.data);  // Log response from backend
                setThankYouMessage("Thank you for your love message!");
            })
            .catch((error) => {
                console.error('There was an error submitting the form!', error);
            });
    };

    return (
        <div className="App">
            <h1>Love Message Form</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Name:</label>
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Rating (1 to 10):</label>
                    <input
                        type="number"
                        value={rating}
                        onChange={(e) => setRating(e.target.value)}
                        min="1"
                        max="10"
                        required
                    />
                </div>
                <div>
                    <label>Your Love Message:</label>
                    <textarea
                        value={loveMessage}
                        onChange={(e) => setLoveMessage(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Submit</button>
            </form>

            {thankYouMessage && <p>{thankYouMessage}</p>}
        </div>
    );
};

export default App;
