import React, { useState } from 'react';
import axios from 'axios';
import './styles.css';

const App = () => {
  const [personalityRatings, setPersonalityRatings] = useState({
    kindness: 0,
    intelligence: 0,
    humor: 0,
    caring: 0,
  });
  const [loveMessage, setLoveMessage] = useState('');
  const [result, setResult] = useState('');

  // Handle personality ratings change
  const handleRatingChange = (e) => {
    setPersonalityRatings({
      ...personalityRatings,
      [e.target.name]: e.target.value,
    });
  };

  // Handle love message change
  const handleMessageChange = (e) => {
    setLoveMessage(e.target.value);
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = {
      personalityRatings,
      loveMessage,
    };

    try {
      const response = await axios.post('http://localhost:5000/submit', formData);
      setResult(response.data.message); // Thank-you message from backend
      setTimeout(() => setResult(''), 5000); // Clear message after 5 seconds
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  return (
    <div className="app">
      <h1>Rate Your Love!</h1>
      <form onSubmit={handleSubmit}>
        <div className="rating">
          <label>Kindness:</label>
          <input
            type="number"
            name="kindness"
            min="0"
            max="10"
            value={personalityRatings.kindness}
            onChange={handleRatingChange}
          />
        </div>
        <div className="rating">
          <label>Intelligence:</label>
          <input
            type="number"
            name="intelligence"
            min="0"
            max="10"
            value={personalityRatings.intelligence}
            onChange={handleRatingChange}
          />
        </div>
        <div className="rating">
          <label>Humor:</label>
          <input
            type="number"
            name="humor"
            min="0"
            max="10"
            value={personalityRatings.humor}
            onChange={handleRatingChange}
          />
        </div>
        <div className="rating">
          <label>Caring:</label>
          <input
            type="number"
            name="caring"
            min="0"
            max="10"
            value={personalityRatings.caring}
            onChange={handleRatingChange}
          />
        </div>

        <div className="message">
          <label>Love Message:</label>
          <textarea
            value={loveMessage}
            onChange={handleMessageChange}
            rows="4"
            cols="50"
          />
        </div>

        <button type="submit">Submit</button>
      </form>

      {result && <p className="result">{result}</p>}
    </div>
  );
};

export default App;
