import React, { useState } from 'react';
import axios from 'axios';

const LoveGame = () => {
  const [personalityRatings, setPersonalityRatings] = useState({
    kindness: 0,
    humor: 0,
    intelligence: 0,
    creativity: 0,
    empathy: 0,
    loyalty: 0
  });
  const [loveMessage, setLoveMessage] = useState('');
  const [result, setResult] = useState('');

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prepare form data
    const formData = {
      personalityRatings,
      loveMessage
    };

    try {
      // Send data to the backend
      const response = await axios.post('http://localhost:5000/submit', formData);

      // Display success message or the response from the backend
      setResult(response.data.message); // assuming the backend sends a message
    } catch (error) {
      console.error("Error submitting form", error);
      setResult("Oops! Something went wrong.");
    }
  };

  return (
    <div className="love-game">
      <h1>Love Game</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Kindness:</label>
          <input
            type="number"
            value={personalityRatings.kindness}
            onChange={(e) => setPersonalityRatings({ ...personalityRatings, kindness: e.target.value })}
            min="0"
            max="10"
          />
        </div>
        <div>
          <label>Humor:</label>
          <input
            type="number"
            value={personalityRatings.humor}
            onChange={(e) => setPersonalityRatings({ ...personalityRatings, humor: e.target.value })}
            min="0"
            max="10"
          />
        </div>
        <div>
          <label>Intelligence:</label>
          <input
            type="number"
            value={personalityRatings.intelligence}
            onChange={(e) => setPersonalityRatings({ ...personalityRatings, intelligence: e.target.value })}
            min="0"
            max="10"
          />
        </div>
        <div>
          <label>Creativity:</label>
          <input
            type="number"
            value={personalityRatings.creativity}
            onChange={(e) => setPersonalityRatings({ ...personalityRatings, creativity: e.target.value })}
            min="0"
            max="10"
          />
        </div>
        <div>
          <label>Empathy:</label>
          <input
            type="number"
            value={personalityRatings.empathy}
            onChange={(e) => setPersonalityRatings({ ...personalityRatings, empathy: e.target.value })}
            min="0"
            max="10"
          />
        </div>
        <div>
          <label>Loyalty:</label>
          <input
            type="number"
            value={personalityRatings.loyalty}
            onChange={(e) => setPersonalityRatings({ ...personalityRatings, loyalty: e.target.value })}
            min="0"
            max="10"
          />
        </div>

        <div>
          <label>Your Love Message:</label>
          <textarea
            value={loveMessage}
            onChange={(e) => setLoveMessage(e.target.value)}
            placeholder="Write a sweet message"
          />
        </div>

        <button type="submit">Submit</button>
      </form>

      {/* Displaying the result after submission */}
      {result && <p>{result}</p>}
    </div>
  );
};

export default LoveGame;
