import React, { useState } from 'react';
import './App.css';

function App() {
  const [ratings, setRatings] = useState({
    love: 5,
    kindness: 5,
    humor: 5,
    patience: 5,
    intelligence: 5,
    honesty: 5,
  });
  const [message, setMessage] = useState('');
  const [response, setResponse] = useState('');

  // Handle ratings change
  const handleRatingChange = (e) => {
    setRatings({ ...ratings, [e.target.name]: e.target.value });
  };

  // Handle message change
  const handleMessageChange = (e) => {
    setMessage(e.target.value);
  };

  // Submit the form
  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = {
      ratings,
      message,
    };

    // Send data to the backend
    const response = await fetch('http://localhost:5000/api/submit', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    });

    const result = await response.json();
    setResponse(result.responseMessage);
  };

  return (
    <div className="App">
      <h1>Rate My Personality</h1>
      <form onSubmit={handleSubmit}>
        <div className="rating-group">
          {Object.keys(ratings).map((trait) => (
            <div key={trait} className="rating-item">
              <label>{trait.charAt(0).toUpperCase() + trait.slice(1)}: </label>
              <input
                type="number"
                name={trait}
                min="1"
                max="10"
                value={ratings[trait]}
                onChange={handleRatingChange}
              />
            </div>
          ))}
        </div>

        <div className="message">
          <label>Your Love Message: </label>
          <textarea
            value={message}
            onChange={handleMessageChange}
            placeholder="Enter your beautiful love message..."
          />
        </div>

        <button type="submit">Submit</button>
      </form>

      {response && <div className="response-message">{response}</div>}
    </div>
  );
}

export default App;
