import React, { useState } from "react";
import axios from "axios";
import "../styles.css";

const LoveForm = () => {
  const [formData, setFormData] = useState({
    name: "",
    rating: "",
    message: "",
  });

  const [responseMessage, setResponseMessage] = useState("");

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/submit", formData);
      setResponseMessage(`Thank you, ${formData.name}! üíñ Your message has been received!`);
    } catch (error) {
      setResponseMessage("Oops! Something went wrong. Please try again later.");
    }
  };

  return (
    <div className="love-form-container">
      <h1>üíñ Welcome, Pando (RGNIKITHA)! üíñ</h1>
      <p className="fun-nicknames">
        AKA: Thakkali, Kutta, and your favorite troublemaker! üåü
      </p>
      <form onSubmit={handleSubmit}>
        <label>Name (Your admirer üòç):</label>
        <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Your Name" required />

        <label>Rating (1-10 for me ü•∫):</label>
        <input type="number" name="rating" value={formData.rating} onChange={handleChange} placeholder="Rate me!" min="1" max="10" required />

        <label>Love Message (Tell me your ‚ù§Ô∏è):</label>
        <textarea name="message" value={formData.message} onChange={handleChange} placeholder="Write your message..." required />

        <button type="submit">Send My Love üöÄ</button>
      </form>
      {responseMessage && <div className="thank-you-message">{responseMessage}</div>}
    </div>
  );
};

export default LoveForm;
